# check if url is valid if [[ -z "${title}" ]]; then notificator "The url is invalid" exit 1 else notificator "Downloading “${shorttitle}”" fi }
getfile() { python youtube-dl --quiet --output "${downdir}/%(title)s.%(ext)s" "${link}" }
